import React from 'react';
import NuffCoffeeCup from './NuffCoffeeCup';

class NuffCoffee extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      coffeecount: 0
    };
  }
  coffeeCounterMinus = (chilData) => {
    console.log(this.state.coffeecount)
    this.props.coffee({
      coffeecount: this.state.coffeecount - 1
    })
  }

  coffeeCounterPlus = () => {
    console.log(this.state.coffeecount)
    this.props.coffee({
      coffeecount: this.state.coffeecount + 1
    })
  }


  render() {
    return (
      <div className="coffeecupcontainer">
        <a href="#" onClick={this.coffeeCounterPlus}><NuffCoffeeCup coffee={this.state.coffeecount}/></a>
      </div>)
  }
}
export default NuffCoffee;
